<script>

import AuthModal from '~/components/user/AuthModal.vue'
import LogoutButton from '~/components/user/LogoutButton.vue'
import Sections from '~/components/catalog/Sections.vue'
import SearchForm from '~/components/catalog/search/SearchForm.vue'
import HeaderMenu from '~/components/header/HeaderMenu.vue'
import BasketSmall from '~/components/header/basket.small/Basket.vue'
import DeliveryRegion from '~/components/header/DeliveryRegion.vue'
import ItemAdded from '~/components/catalog/item/ItemAdded.vue'
import BuyClick from '~/components/catalog/buy1click.vue'

import { mapGetters } from 'vuex'
import basketInit from '~/mixins/basket/init.js'
import qs from 'qs'


export default {
    mixins: [basketInit],
    data() {
        return {
            featuresHidden: true,
            callRequestOk: false,
            rule: [],
            ruleError: false,
            request: {
                name:'',
                phone:''
            }
        }
    },
    components: {
        AuthModal,
        Sections,
        HeaderMenu,
        BasketSmall,
        SearchForm,
        LogoutButton,
        DeliveryRegion,
        ItemAdded,
        BuyClick
    },
    methods: {
        submitRequest() {
            try {
            	window['yaCounter56402599'].reachGoal('backСall');
            } catch(e) {}
            if(this.rule == 1) 
                this.ruleError = false
            else
                this.ruleError = true

            var params = {
                'WEB_FORM_ID':'1',
                'sessid':this.getSessid,
                'form_text_1':this.request.name,
                'form_text_2':this.request.phone,
                'web_form_submit':'Y'
            }
            if(!this.ruleError) {
            	this.$axios.post('/api/v1/forms/', qs.stringify(params)).then( response => {
            	    if (response.data.FORM_NOTE) {
            	        this.callRequestOk = true;
            	    }
            	});
            }
        },
        documentClick(e) {
            let target = e.target,
                features = this.$refs.featuresToggle;

            if ((features !== target) && !features.contains(target)) {
                this.featuresHidden=true;
            }
        }
    },
    computed: {
        ...mapGetters({
            isLogged: 'user/isLogged',
            favoritesCount: 'catalog/getCountFavorites',
            compareCount: 'catalog/getCountCompare',
            getSessid: 'order/getSessid',
            city: 'user/getCity'
        })
    },
    mounted() {
        Promise.all([
            this.$store.dispatch('catalog/GET_FAVORITES'),
            this.$store.dispatch('catalog/GET_COMPARE')
        ])
        window.addEventListener('click', this.documentClick)
    },
    beforeDestroy () {
        window.removeEventListener('click', this.documentClick)
    }
}
</script>

